import React, { useState } from 'react';

const PicturesGrid = () => {
  const pictures = [
    '/assets/images/pic (1).jpg',
    '/assets/images/pic (2).jpg',
    '/assets/images/pic (3).jpg',
    '/assets/images/pic (4).jpg',
    '/assets/images/pic (5).jpg',
    '/assets/images/pic (6).jpg',
    '/assets/images/pic (7).jpg',
    '/assets/images/pic (8).jpg',
    '/assets/images/pic (9).jpg',
    '/assets/images/pic (10).jpg',
    '/assets/images/pic (11).jpg',
    '/assets/images/pic (12).jpg',
    '/assets/images/pic (13).jpg',
  ];
  const [selectedPicture, setSelectedPicture] = useState(null);
  const [showModal, setShowModal] = useState(null);
  const handleSelectPicture = (picture) => {
    setSelectedPicture(picture);
    setShowModal(true);
  };
  return (
    <>
      <div className='picture-grid'>
        {pictures.map((picture, index) => (
          <div
            onClick={() => handleSelectPicture(index)}
            className='picture'
            key={index}
          >
            <img
              className='grid-img'
              src={picture}
              alt={`Picture ${index + 1}`}
            />
          </div>
        ))}
      </div>

      {selectedPicture && showModal && (
        <div
          class='modal fade'
          id='exampleModal'
          tabindex='-1'
          aria-labelledby='exampleModalLabel'
          aria-hidden='true'
        >
          <div class='modal-dialog'>
            <div class='modal-content'>
              <div class='modal-header'>
                <h5 class='modal-title' id='exampleModalLabel'>
                  Modal title
                </h5>
                <button
                  type='button'
                  class='btn-close'
                  data-bs-dismiss='modal'
                  aria-label='Close'
                ></button>
              </div>
              <div class='modal-body'>...</div>
              <div class='modal-footer'>
                <button
                  type='button'
                  class='btn btn-secondary'
                  data-bs-dismiss='modal'
                >
                  Close
                </button>
                <button type='button' class='btn btn-primary'>
                  Save changes
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default PicturesGrid;
