import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.min.css';
import 'swiper/swiper.min.css';
import SwiperCore, { Autoplay, Pagination } from 'swiper';

SwiperCore.use([Autoplay, Pagination]);
const Bride = () => {
  return (
    <div className='bride-container'>
      <div className='stories-img-container'>
        <img
          style={{
            objectFit: 'cover',
            width: '100rem',
            height: '25rem',
          }}
          src='/assets/images/background.jpg'
          alt='bride stories'
        />
      </div>
      <div>
        <h2 className='groom-title'>Bride</h2>
        <article className='groom-paragraph'>
          Out of the blues I saw a request, I checked it and it was this
          beautiful lady. I accepted it immediately and started checking her TL.
          On the 4th of April 2016, I sent her a DM and we started talking. We
          became really really close. Later in November I asked her to be my
          girlfriend and here we are todayðŸ¥° getting married.
        </article>
      </div>
    </div>
  );
};
const Groom = () => {
  return (
    <div className='bride-container'>
      <div className='stories-img-container'>
        <img
          style={{
            objectFit: 'cover',
            width: '100rem',
            height: '25rem',
          }}
          src='/assets/images/background.jpg'
          alt='bride stories'
        />
      </div>
      <div>
        <h2 className='groom-title'>Groom</h2>
        <article className='groom-paragraph'>
          Out of the blues I saw a request, I checked it and it was this
          beautiful lady. I accepted it immediately and started checking her TL.
          On the 4th of April 2016, I sent her a DM and we started talking. We
          became really really close. Later in November I asked her to be my
          girlfriend and here we are todayðŸ¥° getting married.
        </article>
      </div>
    </div>
  );
};
const Stories = () => {
  return (
    <Swiper
      spaceBetween={30}
      slidesPerView={1}
      loop={true}
      autoplay={{
        delay: 5000, // Autoplay every 5 seconds
        disableOnInteraction: false, // Enable navigation and pagination while autoplay
      }}
      pagination={{
        clickable: true,
      }}
      //   spaceBetween={50}
      //   slidesPerView={1}
      //   centeredSlides
      //   onSlideChange={() => console.log('slide change')}
      //   onSwiper={(swiper) => console.log(swiper)}
    >
      <SwiperSlide>
        <Bride />{' '}
      </SwiperSlide>
      <SwiperSlide>
        <Bride />{' '}
      </SwiperSlide>
    </Swiper>
  );
};

export default Stories;
