import AddToCalendar from 'react-add-to-calendar';
import QRCode from 'qrcode.react';
import useSWR from 'swr';

import Head from '@src/components/Head';
import resolvePath from '@src/utils/resolvePath';
import appConfig from '@src/config/app';
import { useTranslation, defaultLocale } from '@src/i18n';
import guestList from './guest_list.json';
import { useState } from 'react';
import Swal from 'sweetalert2';

const translateConfig = (appConfig, locale) => {
  if (!locale || locale === defaultLocale) {
    return appConfig;
  }
  // Replace config with lang
  const configLang = appConfig.lang[locale];
  if (configLang === undefined) {
    throw new Error('invalid locale: ', locale);
  }
  return { ...appConfig, ...configLang };
};

const ShowInvite = ({ currentUrl, guestListLastUpdatedAt, guest }) => {
  const t = useTranslation(guest.locale);

  // Initiate config variables
  const {
    logo,
    ogTags,
    coupleInfo,
    venue,
    weddingDay,
    weddingDate,
    weddingTime,
    calendarInfo,
  } = translateConfig(appConfig, guest.locale);
  const { brideName, groomName, hashtag, coupleNameFormat } = coupleInfo;

  const coupleNameStr =
    coupleNameFormat === 'GROOM_FIRST'
      ? `${groomName} & ${brideName}`
      : `${brideName} & ${groomName}`;
  const coupleName =
    coupleNameFormat === 'GROOM_FIRST' ? (
      <>
        {groomName} <span>&amp;</span> {brideName}
      </>
    ) : (
      <>
        {brideName} <span>&amp;</span> {groomName}
      </>
    );

  // Venue info
  const venueBrief = `${venue.name}, ${venue.city}, ${venue.country}`;
  const weddingDateBrief = `${weddingDay}, ${weddingDate}`;

  // Event info
  const eventTitle = `${coupleNameStr}'s Wedding`;
  let eventDescription = `${weddingDateBrief} at ${venue.name}, ${venue.city}`;
  if (guest.name) {
    eventDescription = `Dear ${guest.name}, you are cordially invited to our wedding on ${weddingDate} at ${venue.name}. Looking forward to seeing you!`;
  }

  // Calendar payload
  const calendarEvent = {
    title: eventTitle,
    description: eventDescription,
    location: `${venue.city}, ${venue.country}`,
    startTime: calendarInfo.timeStartISO,
    endTime: calendarInfo.timeEndISO,
  };

  const [name, setName] = useState('');
  const [attending, setAttending] = useState('');
  const [email, setEmail] = useState('');

  const [isLoading, setIsLoading] = useState(false);

  const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
  const scriptUrl =
    'https://script.google.com/macros/s/AKfycbxapNtm8Jv2QCNYt4CqGJ_mVpEEKERO9fXU8HrTexuDWthmxiQF9NFck6rIjPZ-LgdRHg/exec';

  function httpStatus(statusCode) {
    if (statusCode >= 200 && statusCode <= 299) {
      Swal.fire('Success!', 'We appreciate your response!', 'success');
    } else if (statusCode >= 400 && statusCode <= 499) {
      Swal.fire('Error!', 'Something went wrong, please try again!', 'error');
    } else if (statusCode >= 500) {
      Swal.fire('Error!', 'Something went wrong, please try again!', 'error');
    } else {
      return 'unknown status';
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !email || !attending) {
      Swal.fire('', 'All fields are required', 'error');
      return;
    }
    setIsLoading(true);
    try {
      const response = await fetch(scriptUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, attending }),
      });

      const data = await response.json();
      if (data.success) {
        httpStatus(response.status); // Handle success (e.g., show a success message)
      } else {
        Swal.fire('Error!', 'Something went wrong, please try again!', 'error');
      }
      setAttending('');
      setEmail('');
      setName('');

      setIsLoading(false);
    } catch (error) {
      Swal.fire('Error!', 'Something went wrong, please try again!', 'error');
      console.error(error);
    }
  };

  // const handleSubmit = async (e) => {
  //   e.preventDefault();

  //   if (!name || !email || !attending) {
  //     Swal.fire('', 'All fields are required', 'error');
  //     return;
  //   }
  //   setIsLoading(true);
  //   try {
  //     const response = await fetch(proxyUrl + scriptUrl, {
  //       method: 'POST',
  //       headers: {
  //         'Content-Type': 'application/json',
  //       },
  //       body: JSON.stringify({ name, email, attending }),
  //     });

  //     console.log(response, 'reponse');
  //     const data = await response.text();
  //     httpStatus(response.status);

  //     setAttending('');
  //     setEmail('');
  //     setName('');
  //     // Response from Google Script
  //     setIsLoading(false);
  //   } catch (error) {
  //     setIsLoading(false);
  //     Swal.fire('Error!', 'Something went wrong, please try again!', 'error');
  //     console.error('Error:', error);
  //   }
  // };

  return (
    <div>
      <style jsx global>
        {`
          a.react-add-to-calendar__button span {
            cursor: pointer;
            text-decoration: underline;
          }
        `}
      </style>
      <Head
        {...ogTags}
        title={eventTitle}
        description={eventDescription}
        guestName={guest.name}
        url={currentUrl}
        date={weddingDateBrief}
        modifiedTime={guestListLastUpdatedAt}
        venue={venueBrief}
        logo={resolvePath(ogTags.logo)}
        author={resolvePath('/')}
      />
      <section className='header_area'>
        <div id='home' className='header_slider'>
          <div className='slick-list draggable'>
            <div className='slick-track' style={{ opacity: 1 }}>
              <div
                className='single_slider bg_cover d-flex align-items-center'
                style={{
                  height: '100vh',
                }}
              >
                <div className='container'>
                  <div className='row justify-content-center'>
                    <div className='col-lg-10'>
                      <div
                        className='slider_content text-center'
                        style={{ paddingTop: 0 }}
                      >
                        <img
                          style={{ maxHeight: 60, margin: 25, marginTop: 0 }}
                          src={logo.headerLogo}
                          alt='logo'
                        />
                        <h5
                          className='slider_sub_title'
                          data-animation='fadeInUp'
                          data-delay='0.2s'
                          style={{ animationDelay: '0.2s' }}
                        >
                          {t('siteIntro')}
                        </h5>

                        <h2
                          className='slider_title'
                          data-animation='fadeInUp'
                          data-delay='0.7s'
                          style={{ animationDelay: '0.7s' }}
                        >
                          {coupleName}
                        </h2>
                        <h5
                          className='location'
                          data-animation='fadeInUp'
                          data-delay='1s'
                          style={{ animationDelay: '1s' }}
                        >
                          {hashtag}
                        </h5>
                        <span
                          className='location'
                          data-animation='fadeInUp'
                          data-delay='1s'
                          style={{ animationDelay: '1s' }}
                        >
                          {venue.name}, {venue.city}, {venue.country}.
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      <section id='coming_soon' className='coming_soon_area pt-20 pb-70'>
        <div className='coming_soon_shape_1' style={{ zIndex: 1 }}>
          <img src='/assets/images/shape-1.png' alt='shape' />
        </div>
        <div className='container'>
          <div className='row align-items-center'>
            <div className='col-lg-4'>
              <div
                className='section_title pt-50 wow fadeIn'
                data-wow-duration='1.3s'
                data-wow-delay='0.2s'
                style={{
                  visibility: 'visible',
                  animationDuration: '1.3s',
                  animationDelay: '0.2s',
                  animationName: 'fadeIn',
                }}
              >
                <h3 className='title'>{t('eventDate')}:</h3>
                <p>{weddingDateBrief}</p>
                <div
                  style={{
                    paddingTop: '0.2rem',
                    paddingBottom: '0.2rem',
                  }}
                >
                  <AddToCalendar
                    event={calendarEvent}
                    buttonLabel={t('btnAddToMyCalendar')}
                  />
                </div>
                <img src='assets/images/section_shape.png' alt='Shape' />
              </div>
            </div>
            <div className='col-lg-8'>
              <div
                className='wow fadeIn'
                data-wow-duration='1.3s'
                data-wow-delay='0.6s'
                style={{
                  visibility: 'visible',
                  animationDuration: '1.3s',
                  animationDelay: '0.6s',
                  animationName: 'fadeIn',
                }}
              >
                <div className='coming_soon_count d-flex justify-content-end pt-20'>
                  <div
                    style={{
                      marginRight: 20,
                      width: 360,
                      height: 138,
                      backgroundColor: 'transparent',
                    }}
                    className='single_count d-flex align-items-center justify-content-center mt-30'
                  >
                    <div
                      className='count_content'
                      style={{ zIndex: 1, paddingTop: 20 }}
                    >
                      <a href={venue.mapUrl}>
                        <img
                          style={{ borderRadius: 5 }}
                          src='assets/images/bevent.png'
                          alt='bevent center'
                        />
                      </a>
                      <a
                        href={venue.mapUrl}
                        style={{
                          maxWidth: '75vw',
                          overflowX: 'hidden',
                          textOverflow: 'ellipsis',
                          marginTop: 10,
                        }}
                      >
                        {venue.mapUrl}
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className='coming_soon_shape_2'>
          <img src='/assets/images/shape-2.png' alt='shape' />
        </div>
      </section>

      <section id='contact' className='contact_area'>
        <div className='container'>
          <div
            className='contact_wrapper wow fadeInUpBig'
            data-wow-duration='1.3s'
            data-wow-delay='0.4s'
            style={{
              paddingBottom: 30,
              boxShadow: 'none',
              visibility: 'visible',
              animationDuration: '1.3s',
              animationDelay: '0.4s',
              animationName: 'fadeInUp',
            }}
          >
            <div className='row justify-content-center'>
              <div className='col-lg-9'>
                <div className='section_title text-center pb-30'>
                  {guest.name && (
                    <div
                      style={{
                        textAlign: 'center',
                        maxWidth: 400,
                        margin: 'auto',
                        paddingBottom: 20,
                      }}
                    >
                      {t('invitationGreeting')}
                      <p style={{ fontSize: '1.5rem' }}>{guest.name},</p>
                    </div>
                  )}
                  <h3 className='title'>{t('invitationIntro')}</h3>
                  <div
                    style={{
                      textAlign: 'left',
                      paddingTop: 20,
                      paddingBottom: 20,
                      maxWidth: 400,
                      margin: 'auto',
                    }}
                  >
                    <p
                      style={{
                        fontSize: '1rem',
                        lineHeight: 'inherit',
                        color: 'dimgrey',
                        textAlign: t('invitationContentTextAlign'),
                      }}
                    >
                      <i>
                        {t('invitationContent')}
                        {t('invitationOutro') &&
                          !t('invitationOutro').startsWith('[missing') && (
                            <>
                              <br />
                              <br />
                              {t('invitationOutro')}
                            </>
                          )}
                      </i>
                    </p>
                  </div>

                  {appConfig.showQrCode && guest.name && (
                    <div style={{ marginTop: 20, marginBottom: 35 }}>
                      <QRCode value={guest.guestId} />
                    </div>
                  )}

                  <p className='text'>
                    <a
                      href={venue.mapUrl}
                      style={{
                        borderBottom: '0.2rem solid',
                        marginBottom: 10,
                      }}
                    >
                      <b>{venue.name}</b>
                    </a>
                    <br />
                    {venue.addressLine1}
                    <br />
                    {/* {venue.addressLine2}
                    <br /> */}
                    {venue.country}.
                  </p>
                  <p className='text' style={{ marginTop: 10 }}>
                    <b>
                      {weddingDate}  {weddingTime}
                    </b>
                  </p>
                  {/* attending form */}
                  <div className='container mt-5'>
                    <h2 className='form_title mb-2'>
                      Inform Us of your availability
                    </h2>
                    <form onSubmit={handleSubmit}>
                      <div className='row mb-3'>
                        <div className='col-md-6'>
                          <label htmlFor='name' className='form-label'>
                            Name:
                          </label>
                          <input
                            type='text'
                            className='form-control'
                            id='name'
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                          />
                        </div>
                        <div className='col-md-6'>
                          <label htmlFor='email' className='form-label'>
                            Email:
                          </label>
                          <input
                            type='email'
                            className='form-control'
                            id='email'
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                          />
                        </div>
                      </div>
                      <div className='mb-3'>
                        <label htmlFor='attending' className='form-label'>
                          Will you be attending?
                        </label>
                        <textarea
                          type='number'
                          className='form-control'
                          id='attending'
                          value={attending}
                          onChange={(e) => setAttending(e.target.value)}
                        />
                      </div>

                      <button type='submit' className='btn btn-primary'>
                        {isLoading ? 'Submitting...' : 'Submit'}
                      </button>
                    </form>
                  </div>
                  {/* response modal */}

                  {t('invitationClosing') &&
                    !t('invitationClosing').startsWith('[missing') && (
                      <p
                        className='text'
                        style={{
                          fontStyle: 'italic',
                          maxWidth: 420,
                          margin: 'auto',
                          marginTop: 60,
                        }}
                        dangerouslySetInnerHTML={{
                          __html: t('invitationClosing'),
                        }}
                      ></p>
                    )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Footer section */}
      <footer id='footer' className='footer_area'>
        <div className='footer_shape_1'>
          <img src='/assets/images/shape-1.png' alt='shape' />
        </div>
        <div className='container'>
          <div className='footer_widget pt-50 pb-10 text-center'>
