import React, { useEffect, useState } from 'react';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import ColorThief from 'react-color-thief';
const PicturesGrid = () => {
  const pictures = [
    '/assets/images/pic (1).jpg',
    '/assets/images/pic (2).jpg',
    '/assets/images/pic (3).jpg',
    '/assets/images/pic (4).jpg',
    '/assets/images/pic (5).jpg',
    '/assets/images/pic (6).jpg',
    '/assets/images/pic (7).jpg',
    '/assets/images/pic (8).jpg',
    '/assets/images/pic (9).jpg',
    '/assets/images/pic (10).jpg',
    '/assets/images/pic (11).jpg',
    '/assets/images/pic (12).jpg',
    '/assets/images/pic (13).jpg',
  ];
  const [selectedPicture, setSelectedPicture] = useState(null);
  const [dominantColors, setDominantColors] = useState([]);

  const handleSelectPicture = (picture) => {
    setSelectedPicture(picture);
  };

  useEffect(() => {
    const fetchDominantColors = async () => {
      const colors = await Promise.all(
        pictures.map(async (image) => {
          const response = await fetch(image);
          const blob = await response.blob();

          const colorThief = new ColorThief();
          const dominantColor = await colorThief.getColorAsync(blob);

          return `rgb(${dominantColor[0]}, ${dominantColor[1]}, ${dominantColor[2]})`;
        })
      );

      setDominantColors(colors);
    };

    fetchDominantColors();
  }, [pictures]);

  // useEffect(() => {
  //   const fetchDominantColors = async () => {
  //     const colors = await Promise.all(
  //       pictures.map(async (image) => {
  //         const response = await fetch(image);
  //         const blob = await response.blob();

  //         return DominantColor(blob);
  //       })
  //     );

  //     setDominantColors(colors);
  //   };

  //   fetchDominantColors();
  // }, [pictures]);

  return (
    <>
      <div className='picture-grid'>
        {dominantColors.map((picture, index) => (
          <>
            <div
              title='view image'
              data-toggle='modal'
              data-target='#myModal'
              onClick={() => handleSelectPicture(picture)}
              className='picture'
              key={index}
            >
              <LazyLoadImage
                src={pictures[index]}
                className='grid-img'
                effect='blur'
                width={'100%'}
                alt={`Picture ${index + 1}`}
              />
            </div>
          </>
        ))}
      </div>

      {/* {selectedPicture && showModal && ( */}
      <>
        {/* <div>
            <img src={selectedPicture} alt='selected picture' />
          </div> */}
        <div className='modal fade' id='myModal'>
          <div className='modal-dialog'>
            <div className='modal-content'>
              <div className='modal-body'>
                <img
                  style={{
                    height: '70%',
                    objectFit: 'cover',
                  }}
                  src={selectedPicture}
                  alt='selected picture'
                />
              </div>
            </div>
          </div>
        </div>
      </>
      {/* )} */}
    </>
  );
};

export default PicturesGrid;
