import React, { useEffect, useState } from 'react';
import { LazyLoadImage } from 'react-lazy-load-image-component';

const PicturesGrid = () => {
  const pictures = [
    '/assets/images/pic (1).jpg',
    '/assets/images/pic (2).jpg',
    '/assets/images/pic (3).jpg',
    '/assets/images/pic (4).jpg',
    '/assets/images/pic (5).jpg',
    '/assets/images/pic (6).jpg',
    '/assets/images/pic (7).jpg',
    '/assets/images/pic (8).jpg',
    '/assets/images/pic (9).jpg',
    '/assets/images/pic (10).jpg',
    '/assets/images/pic (11).jpg',
    '/assets/images/pic (12).jpg',
    '/assets/images/pic (13).jpg',
  ];
  const [selectedPicture, setSelectedPicture] = useState(null);

  const handleSelectPicture = (picture) => {
    setSelectedPicture(picture);
  };

  const [animatedImages, setAnimatedImages] = useState([]);

  useEffect(() => {
    pictures.forEach(
      (picture, index) => {
        const timer = setTimeout(() => {
          setAnimatedImages((prevState) => [...prevState, index]);
        }, index * 500);

        return () => clearTimeout(timer);
      },
      [pictures]
    );

    const cleanupTimer = setTimeout(() => {
      setAnimatedImages([]);
    }, pictures.length * 500);

    return () => clearTimeout(cleanupTimer);
  }, [pictures]);
  return (
    <>
      <div className='picture-grid'>
        {pictures.map((picture, index) => (
          <>
            <div
              title='view image'
              data-toggle='modal'
              data-target='#myModal'
              onClick={() => handleSelectPicture(picture)}
              className={`picture ${
                animatedImages.includes(index) ? 'fadeInAnimation' : ''
              }`}
              key={index}
            >
              {/* <img
                loading='lazy'
                className='grid-img'
                src={picture}
                alt={`Picture ${index + 1}`}
              /> */}
              <LazyLoadImage
                src={picture}
                className='grid-img'
                effect='blur'
                width={'100%'}
                alt={`Picture ${index + 1}`}
              />
            </div>
          </>
        ))}
      </div>

      {/* {selectedPicture && showModal && ( */}
      <>
        {/* <div>
            <img src={selectedPicture} alt='selected picture' />
          </div> */}
        <div className='modal fade' id='myModal'>
          <div className='modal-dialog'>
            <div className='modal-content'>
              <div className='modal-body'>
                <img
                  style={{
                    height: '70%',
                    objectFit: 'cover',
                  }}
                  src={selectedPicture}
                  alt='selected picture'
                />
              </div>
            </div>
          </div>
        </div>
      </>
      {/* )} */}
    </>
  );
};

export default PicturesGrid;
